// src/pages/MoodSetterPage.jsx

import React, { useState } from 'react';

function MoodSetterPage() {
  const [mood, setMood] = useState('');

  const handleMoodChange = (event) => {
    setMood(event.target.value);
  };

  const getSuggestions = () => {
    if (mood === 'happy') return "Keep spreading joy and positivity!";
    if (mood === 'sad') return "It's okay to feel sad. Take a walk, talk to a friend, or listen to soothing music.";
    if (mood === 'angry') return "Try deep breathing exercises and take a moment to cool down.";
    if (mood === 'anxious') return "Focus on your breath. Take small steps and be kind to yourself.";
    return "Please select your mood.";
  };

  return (
    <div className="flex flex-col items-center justify-center p-8 min-h-screen bg-gradient-to-r from-purple-100 to-blue-100">
      <h2 className="text-3xl font-bold mb-6">How Are You Feeling Today?</h2>

      <select
        value={mood}
        onChange={handleMoodChange}
        className="p-2 border rounded mb-4 text-lg"
      >
        <option value="">-- Select Mood --</option>
        <option value="happy">Happy</option>
        <option value="sad">Sad</option>
        <option value="angry">Angry</option>
        <option value="anxious">Anxious</option>
      </select>

      <div className="p-4 bg-white rounded shadow-md max-w-md text-center">
        <p className="text-lg font-medium">{getSuggestions()}</p>
      </div>
    </div>
  );
}

export default MoodSetterPage;
