// src/pages/MoodSetterPage.jsx
import React, { useState } from 'react';

function MoodSetterPage() {
  const [selectedMood, setSelectedMood] = useState('');
  const [tips, setTips] = useState('');

  const moodTips = {
    happy: 'Enjoy your happiness! Spread the positivity ðŸŒž.',
    sad: 'Itâ€™s okay to feel sad. Talk to a friend or take time to rest. ðŸŒˆ',
    angry: 'Try deep breathing and give yourself space. You got this. ðŸ’ª',
    anxious: 'Ground yourself with slow breaths and mindfulness. You are safe. ðŸŒ¿',
    lonely: 'Reach out to someone you trust or do something that brings you joy. ðŸ¤—',
  };

  const handleMoodSelect = (mood) => {
    setSelectedMood(mood);
    setTips(moodTips[mood]);
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-cover bg-center p-8"
      style={{ backgroundImage: "url('/images/moodsetter-bg.jpg')" }}
    >
      <h2 className="text-4xl font-bold mb-6 text-white drop-shadow-lg">How are you feeling today? ðŸŒ¼</h2>

      {/* Mood buttons */}
      <div className="flex flex-wrap justify-center gap-6 mb-8">
        <button
          onClick={() => handleMoodSelect('happy')}
          className="px-6 py-3 bg-yellow-400 text-white rounded-lg text-lg font-semibold hover:bg-yellow-500"
        >
          ðŸ˜Š Happy
        </button>
        <button
          onClick={() => handleMoodSelect('sad')}
          className="px-6 py-3 bg-blue-400 text-white rounded-lg text-lg font-semibold hover:bg-blue-500"
        >
          ðŸ˜¢ Sad
        </button>
        <button
          onClick={() => handleMoodSelect('angry')}
          className="px-6 py-3 bg-red-400 text-white rounded-lg text-lg font-semibold hover:bg-red-500"
        >
          ðŸ˜  Angry
        </button>
        <button
          onClick={() => handleMoodSelect('anxious')}
          className="px-6 py-3 bg-purple-400 text-white rounded-lg text-lg font-semibold hover:bg-purple-500"
        >
          ðŸ˜Ÿ Anxious
        </button>
        <button
          onClick={() => handleMoodSelect('lonely')}
          className="px-6 py-3 bg-green-400 text-white rounded-lg text-lg font-semibold hover:bg-green-500"
        >
          ðŸ§¸ Lonely
        </button>
      </div>

      {/* Tips area */}
      {selectedMood && (
        <div
          className="max-w-xl bg-white bg-opacity-30 backdrop-blur-md p-6 rounded-lg shadow-xl text-center text-lg text-white"
        >
          <p className="mb-2">You selected: <strong>{selectedMood}</strong></p>
          <p>{tips}</p>
        </div>
      )}
    </div>
  );
}

export default MoodSetterPage;
