// src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const email = e.target.email.value;

    // Dummy login check: only allow registered email
    if (email.endsWith('@gmail.com')) {
      // Redirect to Chatbot
      navigate('/chatbot');
    } else {
      setError('Only registered emails allowed. Please register first!');
    }
  };

  return (
    <div
      className="flex justify-center items-center min-h-screen bg-cover bg-center"
      style={{ backgroundImage: "url('/images/login-bg.jpg')" }}
    >
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded shadow-md space-y-4 w-96"
      >
        <input name="email" type="email" placeholder="Email" className="p-2 border w-full" required />
        <input name="password" type="password" placeholder="Password" className="p-2 border w-full" required />
        <button
          type="submit"
          className="w-full bg-purple-600 text-white p-2 rounded hover:bg-purple-700 transition-transform transform hover:scale-105"
        >
          Login
        </button>

        {/* Error */}
        {error && (
          <div className="mt-4 p-3 bg-red-500 text-white rounded text-center animate-pulse">
            {error}
          </div>
        )}
      </form>
    </div>
  );
}

export default Login;
