// src/components/Header.jsx
import React from 'react';
import { Link } from 'react-router-dom';

function Header() {
  return (
    <div className="flex flex-col bg-gradient-to-r from-purple-700 to-purple-500 text-white shadow-lg">
      {/* Big Header Section */}
      <div className="p-8 text-center">
        <h1 className="text-5xl font-bold mb-4">Emotional Support App</h1>
        <p className="text-lg max-w-3xl mx-auto">
          Your trusted companion for emotional well-being. 
          Chat with our AI friend, track your mood, and receive personalized tips to stay positive and motivated.
        </p>
      </div>

      {/* Navbar */}
      <nav className="bg-purple-800 p-4 flex justify-center space-x-8 text-lg font-semibold shadow-md">
        <Link to="/" className="hover:underline">Home</Link>
        <Link to="/register" className="hover:underline">Register</Link>
        <Link to="/login" className="hover:underline">Login</Link>
        <Link to="/chatbot" className="hover:underline">Chatbot</Link>
        <Link to="/mood-setter" className="hover:underline">Mood Setter</Link>
      </nav>
    </div>
  );
}

export default Header;
