// src/components/Chatbot.jsx
import React, { useState } from 'react';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    const userMessage = { sender: 'user', text: input };
    const botMessage = {
      sender: 'bot',
      text: generateBotReply(input),
    };
    setMessages([...messages, userMessage, botMessage]);
    setInput('');
  };

  const generateBotReply = (text) => {
    if (text.includes('sad')) return "I'm here for you! Want me to suggest something to feel better?";
    if (text.includes('happy')) return "Yay! I'm so glad to hear that!";
    return "Tell me more. I'm listening! ❤️";
  };

  return (
    <div className="w-full max-w-lg">
      <div className="h-96 overflow-y-auto p-4 border rounded bg-white mb-4">
        {messages.map((msg, idx) => (
          <div key={idx} className={`mb-2 text-${msg.sender === 'user' ? 'right' : 'left'}`}>
            <span className={`inline-block p-2 rounded ${msg.sender === 'user' ? 'bg-blue-200' : 'bg-green-200'}`}>
              {msg.text}
            </span>
          </div>
        ))}
      </div>
      <div className="flex">
        <input
          type="text"
          className="flex-grow p-2 border rounded-l"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={handleSend} className="bg-blue-500 text-white px-4 rounded-r hover:bg-blue-600">Send</button>
      </div>
    </div>
  );
}

export default Chatbot;
