// src/pages/Register.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [registered, setRegistered] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Dummy registration logic
    setRegistered(true);

    // After 2 sec â†’ hide popup and redirect to Home
    setTimeout(() => {
      setRegistered(false);
      navigate('/');
    }, 2000);
  };

  return (
    <div
      className="flex justify-center items-center min-h-screen bg-cover bg-center"
      style={{ backgroundImage: "url('/images/register-bg.jpg')" }}
    >
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 rounded shadow-md space-y-4 w-96 relative"
      >
        <input type="text" placeholder="Name" className="p-2 border w-full" required />
        <input type="text" placeholder="Username" className="p-2 border w-full" required />
        <input type="password" placeholder="Password" className="p-2 border w-full" required />
        <input type="number" placeholder="Age" className="p-2 border w-full" required />
        <input type="email" placeholder="Email" className="p-2 border w-full" required />
        <button
          type="submit"
          className="w-full bg-purple-600 text-white p-2 rounded hover:bg-purple-700 transition-transform transform hover:scale-105"
        >
          Register
        </button>

        {/* Popup */}
        {registered && (
          <div className="absolute top-2 left-1/2 transform -translate-x-1/2 bg-green-500 text-white px-4 py-2 rounded shadow animate-bounce">
            ðŸŽ‰ User Registered! Redirecting to Home...
          </div>
        )}
      </form>
    </div>
  );
}

export default Register;
