// src/pages/Chatbotpage.jsx
import React, { useState } from 'react';

function Chatbotpage() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() === '') return;

    const newMessages = [...messages, { text: input, type: 'user' }];

    // Simple bot reply simulation
    const botReply = {
      text: `I'm here for you. You said: "${input}"`,
      type: 'bot',
    };

    setMessages([...newMessages, botReply]);
    setInput('');
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-transparent p-8">
      <h2 className="text-4xl font-bold mb-4 text-purple-800">AI-Powered Chatbot</h2>

      <div className="w-full max-w-lg h-96 overflow-y-auto border rounded bg-white bg-opacity-50 backdrop-blur-md p-4 mb-4 shadow-lg">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`p-2 my-2 rounded ${
              msg.type === 'user' ? 'bg-purple-100 text-right' : 'bg-green-100 text-left'
            }`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <div className="flex w-full max-w-lg space-x-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          className="flex-grow px-4 py-2 border rounded"
        />
        <button
          onClick={handleSend}
          className="bg-purple-700 text-white px-4 py-2 rounded hover:bg-purple-800"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default Chatbotpage;
