// src/pages/MoodSetterPage.jsx

import React, { useState } from 'react';

function MoodSetterPage() {
  const [mood, setMood] = useState('');
  const [tips, setTips] = useState('');

  const handleMoodChange = (e) => {
    const selectedMood = e.target.value;
    setMood(selectedMood);

    // Example tips
    let moodTips = '';
    if (selectedMood === 'happy') {
      moodTips = 'Keep spreading positivity and joy!';
    } else if (selectedMood === 'sad') {
      moodTips = 'It is okay to feel sad. Talk to a friend or try relaxing activities.';
    } else if (selectedMood === 'angry') {
      moodTips = 'Take deep breaths, go for a walk, and express yourself calmly.';
    } else if (selectedMood === 'anxious') {
      moodTips = 'Try mindfulness exercises and focus on things you can control.';
    } else if (selectedMood === 'neutral') {
      moodTips = 'Maintain your balance and take time for self-care.';
    } else {
      moodTips = '';
    }

    setTips(moodTips);
  };

  return (
    <div
      className="flex flex-col items-center justify-center min-h-screen p-8 bg-cover bg-center text-white"
      style={{ backgroundImage: "url('/images/mood-bg.jpg')" }}
    >
      <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-lg p-8 shadow-lg max-w-xl w-full text-center text-black">
        <h2 className="text-3xl font-bold mb-6 text-purple-800">How are you feeling today?</h2>
        <select
          value={mood}
          onChange={handleMoodChange}
          className="w-full p-3 rounded-lg border border-purple-400 focus:outline-none focus:ring-2 focus:ring-purple-600 mb-6"
        >
          <option value="">Select your mood</option>
          <option value="happy">üòä Happy</option>
          <option value="sad">üò¢ Sad</option>
          <option value="angry">üò° Angry</option>
          <option value="anxious">üòü Anxious</option>
          <option value="neutral">üòê Neutral</option>
        </select>

        {mood && (
          <div className="mt-4">
            <h3 className="text-xl font-semibold mb-2 text-purple-700">Tips for you:</h3>
            <p className="text-lg">{tips}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default MoodSetterPage;
