// src/pages/MoodSetterPage.jsx

import React, { useState } from 'react';

function MoodSetterPage() {
  const [mood, setMood] = useState('');

  const handleMoodChange = (event) => {
    setMood(event.target.value);
  };

  const getSuggestions = () => {
    switch (mood) {
      case 'happy':
        return "Stay happy! Spread positivity today üåû‚ú®.";
      case 'sad':
        return "It's okay to feel sad. Try journaling or talking to a loved one üíú.";
      case 'angry':
        return "Take deep breaths, drink some water, and give yourself space to cool down üòå.";
      case 'anxious':
        return "Ground yourself: look around, take slow breaths, and be gentle with yourself üåø.";
      case 'lonely':
        return "Reach out to a friend or family member. You're never alone ‚ù§Ô∏è.";
      case 'tired':
        return "Listen to your body ‚Äî it's okay to rest and recharge üåô.";
      default:
        return "Please select how you are feeling today üí≠.";
    }
  };

  return (
    <div
      className="flex flex-col items-center justify-center min-h-screen p-8 bg-cover bg-center text-white"
      style={{ backgroundImage: "url('/images/mood-bg.jpg')" }} // Add mood-bg.jpg in public/images
    >
      <h2 className="text-4xl font-bold mb-6 bg-purple-700 bg-opacity-70 px-6 py-2 rounded shadow-lg animate-pulse">
        How Are You Feeling Today?
      </h2>

      <select
        value={mood}
        onChange={handleMoodChange}
        className="p-3 mb-6 text-lg rounded shadow-md bg-white text-black focus:outline-none focus:ring-4 focus:ring-purple-400"
      >
        <option value="">-- Select Your Mood --</option>
        <option value="happy">üòä Happy</option>
        <option value="sad">üò¢ Sad</option>
        <option value="angry">üò† Angry</option>
        <option value="anxious">üò∞ Anxious</option>
        <option value="lonely">üòî Lonely</option>
        <option value="tired">üò¥ Tired</option>
      </select>

      <div className="bg-white bg-opacity-80 text-black text-lg font-medium p-6 rounded shadow-xl max-w-xl text-center transition duration-300 ease-in-out">
        {getSuggestions()}
      </div>
    </div>
  );
}

export default MoodSetterPage;
