// src/pages/ChatbotPage.jsx

import React, { useState } from 'react';

function ChatbotPage() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim() === '') return;

    const userMessage = { sender: 'user', text: input };
    const botMessage = { sender: 'bot', text: `I hear you. Remember, I am here for you. You said: "${input}" ❤️` };

    setMessages([...messages, userMessage, botMessage]);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div
      className="flex flex-col items-center justify-center min-h-screen bg-cover bg-center p-8"
      style={{ backgroundImage: "url('/images/chatbot-bg.jpg')" }}
    >
      <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-lg p-6 shadow-lg w-full max-w-2xl h-3/4 overflow-y-auto mb-4 text-black">
        {messages.map((msg, index) => (
          <div key={index} className={`mb-2 ${msg.sender === 'user' ? 'text-right' : 'text-left'}`}>
            <span className={`inline-block px-4 py-2 rounded-lg ${msg.sender === 'user' ? 'bg-purple-300' : 'bg-purple-100'}`}>
              {msg.text}
            </span>
          </div>
        ))}
      </div>

      <textarea
        rows="2"
        placeholder="Type your message here..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        className="w-full max-w-2xl p-3 border rounded bg-white bg-opacity-70 mb-2"
      ></textarea>

      <button onClick={handleSend} className="bg-purple-700 text-white py-2 px-6 rounded hover:bg-purple-800 transition">
        Send
      </button>
    </div>
  );
}

export default ChatbotPage;
